def execute(filters=None):
    columns = [
        {"label": "Student Name", "fieldname": "student_name", "fieldtype": "Data"},
        {"label": "Subject", "fieldname": "subject_name", "fieldtype": "Data"},
        {"label": "Marks", "fieldname": "marks", "fieldtype": "Float"},
    ]

    data = []

    students = frappe.get_all("Student", fields=["name", "name1"])
    student_map = {s["name"]: s["name1"] for s in students}

    subjects = frappe.get_all("Student Subject", fields=["parent", "subject_name", "marks"])

    for row in subjects:
        data.append({
            "student_name": student_map.get(row["parent"]),
            "subject_name": row["subject_name"],
            "marks": row["marks"]
        })

    return columns, data
